{!REQUIRESCRIPT("/soap/ajax/25.0/connection.js")}

var AccountId = "{!Account.Id}"; 

var result = sforce.connection.query("SELECT Id, Name, OppCloseDate__c, Abacus_Payterms__c, CurrencyIsoCode, OwnerId FROM Account WHERE Id = '" +AccountId+ "'"); 
var records = result.getArray("records"); 


//alert (records[0]) 


var OppCloseDate = records[0].OppCloseDate__c;


var OPP = new sforce.SObject("Opportunity"); 
OPP.Name = "Enter Opportunity name"; 
OPP.OwnerId = "{!Account.OwnerId}";
OPP.CurrencyIsoCode = "{!Account.CurrencyIsoCode}"; 
OPP.Opportunity_Account__c = "{!Account.Id}";
OPP.End_Customer_Account__c = "{!Account.Id}";
OPP.AccountId = "{!Account.Id}";
OPP.RecordTypeId = "01220000000CqRn";/*Record ID f√ºr Opportunity*/ 
OPP.StageName ="3 Calling On"; 
OPP.CloseDate = OppCloseDate; 
OPP.Market_Success__c = "10% Uncertain"; 
OPP.Description = "(Enter Description)";

var result = sforce.connection.create([OPP]);

//alert(result[0])

if (result[0].success=='false') {
throw new Error(result[0].errors.message);
}
else {
var OpportunityId = result[0].id; 
window.parent.location.href="/"+ OpportunityId +"/e?retURL=%2F"+OpportunityId;
}
