rules.formula:
AND(
/* Only APAC */
  NOT(OR(
    ISPICKVAL($User.uB_Office__c,"Americas"),
    ISPICKVAL($User.uB_Office__c,"EMEA"),
    ISPICKVAL($User.uB_Office__c,"Corporate")
  )),
  OR(
/* All Sales excl. Leaders and Country Managers (manager=:atan:) */
    AND(
      NOT($User.Team_Leader__c),
      $User.ManagerId = "00520000000wCRA"
    ),
/* Team Leader or Technical Manager with Quantity <= 10*/
    AND(
      OR(
        $User.Team_Leader__c,
        $User.ProfileId="00e200000015FVB"
      ),
      Quantity__c<=10
    )
  )
)

AND(
/* Only APAC */
  NOT(OR(
